INSERT INTO `recursos` (`id`, `nombre`, `tipo`, `precio`, `descripcion`, `duracion`, `aforo_maximo`,`horas`) VALUES
(1, 'Museo de la Escuela Rura', 'Museo', 5.25, 'En esta antigua escuela, construida en 1908, el visitante se verá inmerso en una atmósfera que le transportará a otro tiempo. A través de una gran colección de mobiliario y material escolar que data de 1911 hasta 1970, el museo ofrece un recorrido cronológico por la Historia de la Educación en Asturias.', 4, 16,'9:00;13:00'),
(2, 'Casa Museo de Leopoldo', 'Museo', 12.00, 'Ubicado en el pueblo de Piñera, el museo se encuentra en una casa de arquitectura tradicional asturiana. Un recorrido por el mismo nos ofrece una extensa colección de piezas de cerámica popular con una amplia representación de todos los alfares.', 2, 2,'10:30;12:30;16:00'),
(3, 'Que Cocine Valentina', 'Restaurante', 18.99, 'Acogedor restaurante de comida casera y tradicional asturiana. El menú incluye fabada asturiana con compango de la zona y merluza de pincho a la sidra con almejas y mejillones.', 2, 24,'13:00;13:30;14:00;14:45'),
(4, 'Sidrería Casa Cortina', 'Restaurante', 32.50, 'Sidrería con una gran selección de sidras asturianas y platos tradicionales. El menú incluye croquetas de la abuela con jamón serrano y sopa de marisco con percebes y langostinos.', 2, 18, '13:00;13:30;14:00;14:45'),
(5, 'Hostería de Torazo', 'Hostal', 72.00, 'Hotel sencillo con restaurante, helipuerto, spa de día y piscinas exterior y cubierta.', 12, 34,'20:00'),
(6, 'Hotel peñacabrera', 'Hotel', 47.25, 'El Hotel peñacabrera 2 se encuentra en Santa Eulalia y ofrece bar, WiFi gratuita y vistas al jardín.', 12, 25,'20:00'),
(7, 'Pico Aliño desde Naveda', 'Ruta', 1.50, 'Hermosa ruta en la que recorreremos una parte menos frecuentada de Cabranes, pero que aún así recoge la naturaleza y belleza que la caracteriza.', 2, 4,'06:30;09:00'),
(8, 'Ruta de Peña Cabrera', 'Ruta', 0.00, 'El recorrido parte de Santolaya y transcurre entre Villaviciosa y Piloña, por senderos y caminos carreteros a través de fincas, plantaciones de eucalipto y bosques de robles. ', 4, 5,'07:00;08:00'),
(9, 'Senda del Río Viacaba', 'Ruta', 0.00, 'La ruta comienza en la población de Santa Eulalia de Cabranes y termina donde estaba la antigua mina de antracita con su castillete restaurado en Vegapallía.', 1, 3,'10:30'),
(10, 'Iglesia de Santa Eulalia', 'Religioso', 0.00, 'La iglesia de Santa Eulalia de Cabranes es un templo que data del siglo XV, construido sobre una iglesia románica anterior. Cuenta con un campanario cuadrado y se accede a través de dos puertas.', 1, 6,'10:30;11:30;12:30;13:30');
